ow(                  children: [                    Icon(Icons.star, color: Colors.amber, size: 20,),                    SizedBox(width: 3,),                    Text("${widget.rentReviews}",                        style: TextStyle(                            fontSize: 14,                            fontWeight: FontWeight.w400,                            color: Colors.grey                        )                    ),                    SizedBox(width: 3,),                    Text("(${widget.totalReviews})",                        style: TextStyle(                            fontSize: 14,                            fontWeight: FontWeight.w400,                            color: Colors.grey                        )                    )                  ],                ),                SizedBox(height: 10,),                SizedBox(                    width: widget.size.width*.45,                    child: Row(                  mainAxisAlignment: MainAxisAlignment.spaceBetween,                  children: [                    AppButton(                        width: 100,                        height: 35,                        fontSize: 13,                        text: "Rent Now",                        onClick: (){}                    ),                    InkWell(                      onTap: ()async{                        setState(()=> _isAddingToFav = true);                        if(_auth.currentUser == null){                          setState(()=> _isAddingToFav = false);                          AppAler.showMyDialog(message: "You need to login first.", context: context, onClick: ()=>Navigator.push(context, MaterialPageRoute(builder: (context)=>Login())));                          return;                        }else{                          if(_isFav){                            print("remove from fav");                            await WishListController.removeFromFavorite(carId: widget.carId).then((value) =>  _checkIsFavOrNot()); //check if this car is in wishlist or not);                          }else{                            await WishListController.addToFavorite(data:  widget.data!..addAll({"driver_info": _auth.currentUser!.email}));                          }                          _checkIsFavOrNot();                        }                        setState(()=> _isAddingToFav = false);                      },                      child: _isAddingToFav ? CircularProgressIndicator(strokeWidth: 1,) : Icon(Icons.favorite, color: _isFav == true ? AppColors.red : Colors.grey,),                    )                  ],                ))              ],            ))          ],        ),      ),    );  }}